import { useState } from 'react/cjs/react.production.min';
const ExpenseForm = () => {

    const [enteredTiltle, setEnteredTitle] = useState('');
    const [enteredDate, setEnteedDate] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');
    const [userInput, setUserInput] = useState({
        enteredTiltle: '',
        enteredAmount: '',
        enteredDate: '',
    });

    const titleChangeHandler = (event) => {
        setEnteredTitle(event.target.value)
        //     setUserInput({
        //         ...userInput,
        //         enteredTiltle: event.target.value,
        //     });
         };


        const amounChangeHandler = (event) => {
            setEnteredAmount(event.target.value)
            //     setUserInput({
            //         ...userInput,
            //         enteredAmount: event.target.value,
            //     });
            };


            const dateChangeHandler = (event) => {
                setEnteedDate(event.target.value);
                //     setUserInput({
                //         ...userInput,
                //         enteredDate: event.target.value
                //     })
                };


                const submitHandler = (event) => {
                    event.preventDefault();
                    const expenseDate = {
                        title: enteredTiltle,
                        amount: enteredAmount,
                        date: new Date(enteredDate)

        }


                };

                return (
                    <form onSubmit={submitHandler}>
                        <div className='new-expense__controls'>
                            <div lassName='new-expense__control'>
                                <label>Title</label>
                                <input type='text' onChange={titleChangeHandler} />
                            </div>
                            <div lassName='new-expense__control'>
                                <label>Amount</label>
                                <input type='num' min='0.01' step='0.01' onChange={amounChangeHandler} />
                            </div>
                            <div lassName='new-expense__control'>
                                <label>Date</label>
                                <input type='date' min='2019-01-01' max='2022-12-31' onChange={dateChangeHandler} />
                            </div>
                        </div>
                        <div className="new-expense__actions">
                            <button>Add Expense</button>
                        </div>
                    </form>
                )
            };

            export default ExpenseForm;
